package com.codex.eduapp.helper;import java.util.regex.Matcher;import java.util.regex.Pattern;public class Helper {    public boolean emailValidator(String email)    {        Pattern pattern;        Matcher matcher;        final String EMAIL_PATTERN    = "^\\w+([-+.']\\w+)*@\\w+([-.]\\w+)*\\.\\w+([-.]\\w+)*$";        pattern = Pattern.compile(EMAIL_PATTERN);        matcher = pattern.matcher(email);        return matcher.matches();    }}